   public Double Amortizacion(Double Deuda, Double Interes,Double Periodo)
   {
       Double res;
       Double pot;
       Double porcentajeInteres = Interes * 0.01;  
       
       pot =1-((Double) Math.pow((1+porcentajeInteres), (-Periodo)));
       System.out.print(pot+"  ");
       res=Deuda*(porcentajeInteres/(pot));
       System.out.print(res);
       return res;
   }
    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        Double Interes = Double.parseDouble(txtInteres.getText());
        Double Deuda = Double.parseDouble(txtValorDeuda.getText());
        Double Periodos = Double.parseDouble(txtPeriodos.getText());
        Double res=Amortizacion(Deuda,Interes,Periodos);
        txtCuotas.setText(String.valueOf(res));

    }//GEN-LAST:event_jButton1ActionPerformed

